import React, { useState } from 'react';
import Conditions from '../Conditions/Conditions';

const Forecast = () => {
    let [responseObj, setResponseObj] = useState({});
    let [city, setCity] = useState('');
    let [unit, setUnit] = useState('imperial');

    function getForecast() {
        // weather data fetch function will go here
        const options = {
            method: 'GET',
            headers: {
                'X-RapidAPI-Host': 'community-open-weather-map.p.rapidapi.com',
                'X-RapidAPI-Key': 'fb978a9957msh0b56e3055da8ae7p111c46jsnfa747495b5bc'
            }
        };

        fetch('https://community-open-weather-map.p.rapidapi.com/weather?q=Seattle', options)
            .then(response => response.json())
            .then(response => {
                setResponseObj(response)
            })

        if (responseObj.weather[0].main === "Clouds") {
            let appHeaders = document.getElementsByClassName("App-header")
            appHeaders[0].getElementsByClassName.backgroundColor = "indigo";
            console.log("Yolo")
        }
    }

    return (
        // JSX code will go here
        <div>
            <h2>Your local Weather</h2>
            <button onClick={getForecast}>Get Forecast</button>
            <Conditions
                responseObj={responseObj}
            />
        </div>
    )
}

export default Forecast;
