import React from 'react';

const Conditions = (props) => {
    var weatherLogo;

    if (props.responseObj.weather[0].main === "Clouds") {
        weatherLogo = '../images/1.png'
    }

    return (
        <div>
            {(props.responseObj.cod === 200) ?
                <div>
                    <figure>
                        <img src={weatherLogo} alt='clouds' width="200px"/>
                    </figure>
                    <div>
                        <p><strong>{props.responseObj.name}</strong></p>
                        <p>It is currently {(1.8 * (Math.round(props.responseObj.main.temp) - 273)) + 32} degrees F out with {props.responseObj.weather[0].description}.</p>
                    </div>
                </div>
                : null
            }
        </div>
    )
}

export default Conditions;
